package Method;

public class Non_Static_Method {
	
	    //method
		static void printMessage()
		{
			System.out.println("Welcome Java");
		}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//object create
		
		Non_Static_Method obj = new Non_Static_Method();
		
		//call method
		obj.printMessage();
		

		
	}

}
