package Method;

public class Parameter_Program {
	
	static void sum (int a, int b)  //accepts 2 parameters int a, int b
	{
		System.out.println("sum of a & b is:" + (a+b));
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		sum(2,5); // arguments passed to sum method
	}

}
